import { useParams } from "react-router-dom";
import Layout from "./Layout";
import { useEffect, useState } from "react";
import uuid from "react-uuid";
import Sidebar from "./Sidebar";
import Main from "./Main";
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';


const NoteDetails = () =>{
    const {id} = useParams();
    const [notes, setNotes] = useState(JSON.parse(localStorage.notes) || []);
    const [activeNote, setActiveNote] = useState(false);
    const [showSidebar, setShowSidebar] = useState(true);

    useEffect(() => {
        localStorage.setItem("notes", JSON.stringify(notes));
      }, [notes]);


    if (!activeNote) {
        return <div className="not-active-note"><small>No Note Selected</small></div>;
      }
      const onEdit = (key, value) => {
        onUpdateNote({
          ...activeNote,
          [key]: value,
          dateCreated: Date.now(),
        });
      };

    const onAddNote = () => {

      const newNote = {
        id: uuid(),
        title: "Untitled",
        body: "",
        dateCreated: Date.now(),
      };
      setNotes([newNote, ...notes]);
      setActiveNote(newNote.id);
     };


    const onDeleteNote = (idToDelete) => {
      const answer = window.confirm("Are you sure?");
      if (answer) {
        setNotes(notes.filter((note) => note.id !== idToDelete));
      }
    };


    const getActiveNote = () => {
      return notes.find((note) => note.id === activeNote);
    };


    const onUpdateNote = (updatedNote) => {
      const arrayUpdated = notes.map((note) => {
        if (note.id === activeNote) {
          return updatedNote;
        }


        return note;
      });


      setNotes(arrayUpdated);
    };


    const toggleSidebar = () => {
      setShowSidebar(!showSidebar);
    };


    useEffect(() => {
      localStorage.setItem("notes", JSON.stringify(notes));
    }, [notes]);


    return (
      <div className="Layout">
        <div className="header">
          <div>
            <h1 id="menu-sy" className="menu-symb" onClick={toggleSidebar}>
              &#9776;
            </h1>
          </div>
          <div className="main-header">
            <h1>Lotion</h1>
            <p>
              <small>Like Notion, but worse</small>
            </p>
          </div>
        </div>
        <hr></hr>
        <div className="page">
          {showSidebar && (
            <Sidebar
              notes={notes}
              onAddNote={onAddNote}
              activeNote={activeNote}
              setActiveNote={setActiveNote}
            />
          )}
             <div className="app-main">
     <div className="app-main-note-edit">
       <div className="cont-text">
         <div className="left-side">
           <input
             type="text"
             id="title"
             value={activeNote.title}
             onChange={(e) => onEdit("title", e.target.value)}
             autoFocus
           />
           <div className="date-note">
             <input
               className="input-date"
               type="datetime-local"
               value={new Date(Date.now() - new Date().getTimezoneOffset() * 60000)
                 .toISOString()
                 .slice(0, -8)}
               onChange={(e) => onEdit("dateCreated", e.target.value)}
             />
           </div>

         </div>
         <div className="right-side">
           <div id="save">
             <p>Save</p>
           </div>
           <div id="delete">
             <p onClick={() => onDeleteNote(activeNote.id)}>Delete</p>
           </div>
         </div>
         <div className="quill">

           </div>

       </div>
       <div className="quill">
       <ReactQuill
             className="quill-editor"
             value={activeNote.body}
             onChange={(value) => onEdit("body", value)}

           />
           </div>
     </div>


   </div>
        </div>
      </div>
    );
   }





export default NoteDetails;
