function Main({activeNote,onDeleteNote,onUpdateNote}){

    if(!activeNote)return <div className="not-active-note"><small>No Note Selected</small></div>

    const onEdit = (key, value) => {
        const updatedNote = {
          ...activeNote,
          [key]: value,
          dateCreated: key === 'dateCreated' ? value : activeNote.dateCreated
        };
        onUpdateNote(updatedNote);
      };

      const currentDatetime = new Date().toLocaleString('en-US', { timeZone: 'America/Edmonton', hour12: false }).replace(/[/]/g, '-');
      const datetimeLocalDefault = currentDatetime.slice(0, -3);

      return (
        <div className="app-main">
          <div className="app-main-note-edit">
            <div className="cont-text">
              <div className="left-side">
                <input type="text" id="title" value={activeNote.title} onChange={(e) => onEdit('title', e.target.value)} autoFocus />
                <div className="date-note">
                  <input className="input-date" type="datetime-local" defaultValue={datetimeLocalDefault} onChange={(e) => onEdit('dateCreated', new Date(e.target.value).getTime())} />
                  <small>{new Date(activeNote.dateCreated).toLocaleString()}</small>
                </div>
              </div>
              <div className="right-side">
                <div id="save"><p>Save</p></div>
                <div id="delete"><p onClick={() => onDeleteNote(activeNote.id)}>Delete</p></div>
              </div>
            </div>
            <textarea id="body" value={activeNote.body} placeholder="Your Note Here" onChange={(e) => onEdit('body', e.target.value)} />
          </div>
        </div>
      );

}

export default Main;
