import { Link, useNavigate } from 'react-router-dom';

function Sidebar({ notes, onAddNote, activeNote, setActiveNote }) {
  const noNotesMessage = notes.length === 0 && <div id="no-note">No Notes Yet</div>;
  const navigate = useNavigate();

  const onAddNoteClick = () => {
    const newNote = {
      id: uuid(),
      title: "Untitled",
      body: "",
      dateCreated: Date.now(),
    };
    setNotes([newNote, ...notes]);
    setActiveNote(newNote.id);
    navigate(`/notes/${newNote.id}/edit`); // navigate to new note's edit page
  };

  return (
    <div className="app-sidebar">
      <div className="app-sidebar-header">
        <h1>Notes</h1>
        <Link to="#" onClick={onAddNoteClick} className="plus-symb">
          &#43;
        </Link>
      </div>
      <div className="app-sidebar-notes">
        {noNotesMessage}
        {notes.map((note) => (
          <Link
            to={`/notes/${note.id}`}
            className={`app-sidebar-note ${note.id === activeNote && "active"}`}
            onClick={() => setActiveNote(note.id)}
            key={note.id}
          >
            <div className="sidebar-note-title">
              <strong>{note.title}</strong>
            </div>
            <small className="note-meta">
              {new Date(note.dateCreated).toLocaleDateString("en-US", {
                month: "long",
                day: "numeric",
                year: "numeric",
              })}
            </small>
          </Link>
        ))}
      </div>
    </div>
  );
}


export default Sidebar;
