import React, { useState ,useRef} from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import {useNavigate} from 'react-router-dom';

function Main({ activeNote, onDeleteNote, onUpdateNote }) {
  const [isEditing, setIsEditing] = useState(false);
  const quillref = useRef(null);
  if (!activeNote) {
    return <div className="not-active-note"><small>No Note Selected</small></div>;
  }

  const onEdit = (key, value) => {
    onUpdateNote({
      ...activeNote,
      [key]: value,
      dateCreated: Date.now(),
    });
  };
  const handleSave = () => {
  onSave();
  setIsEditing(false);
}


   const onSave = () => {
       const html = quillref.current.getEditor().container.firstChild.innerHTML;
       const title = document.querySelector('.note-edit-head input[type="text"]').value;
       let content = {
           id: activeNote.id,
           body: html,
           title: title,
           dateCreated: new Date(Date.now() - new Date().getTimezoneOffset() * 60000).toISOString().slice(0, -8),
       }
       onUpdateNote(content);
   }






  return (
    <div className="app-main">
      <div className="app-main-note-edit">
        <div className="cont-text">
          <div className="left-side">
            <input
              type="text"
              id="title"
              value={activeNote.title}
              onChange={(e) => onEdit("title", e.target.value)}
              autoFocus
            />
            <div className="date-note">
              <input
                className="input-date"
                type="datetime-local"
                value={new Date(Date.now() - new Date().getTimezoneOffset() * 60000)
                  .toISOString()
                  .slice(0, -8)}
                onChange={(e) => onEdit("dateCreated", e.target.value)}
              />
            </div>
          </div>
          <div className="right-side">
            <div id="save">
              <p onClick={() => setIsEditing(!isEditing)}>{isEditing ? 'Save' : 'Edit'}</p>
            </div>
            <div id="delete">
              <p onClick={() => onDeleteNote(activeNote.id)}>Delete</p>
            </div>
          </div>
        </div>
        {isEditing ? (
          <ReactQuill
            ref={quillref}

            className="quill-editor"
            placeholder='Enter Your Note Here'
            value={activeNote.body}
            onChange={(value) => onEdit("body", value)}
          />
        ) : (
          <p className="quill-content">{activeNote.body && activeNote.body.replace(/<[^>]+>/g, '')}</p>
        )}
      </div>
    </div>
  );
}

export default Main;
