function Sidebar({notes,onAddNote,onNoteSelect,activeNote,setActiveNote}){

if(notes.length)

return( <div className="app-sidebar">
<div className="app-sidebar-header">
<h1>Notes</h1>
<p onClick ={onAddNote} className="plus-symb">&#43;</p>

</div>
<div className="app-sidebar-notes">
    <div id="no-note">No Notes Yet</div>
    {notes.map((note)=> (

        <div className={`app-sidebar-note ${note.id===activeNote && "active"}`}onClick={()=>setActiveNote(note.id)}>
        <div className="sidebar-note-title">
            <strong>{note.title}</strong>
        </div>
        <small className="note-meta">
    {new Date(note.dateCreated).toLocaleDateString("en-US", {
        month: "long",
        day: "numeric",
        year: "numeric",
        hour: "numeric",
        minute: "numeric",
        hour12: true
    })}
</small>

        <p>{note.body&& note.body.substr(0,20)+"..."}</p>
    </div>
    ))}
</div>
</div>);
}

export default Sidebar;
