import {useState} from "react";
function Main({notes,onAddNote,onDeleteNote}){
    const [currentNoteId, setCurrentNoteId] = useState(null);
    const handleDeleteNote = () => {
      onDeleteNote(currentNoteId);
    };
    return (
      <div className="app-main">
        <div className="app-main-note-edit">
          <div className="cont-text">
            <div className="left-side">
              <strong><input type="text" id="title" placeholder="Untitled" autoFocus /></strong>
              <div className="date-note"><small>Date</small></div>
            </div>
            <div className="right-side">
              <div id="save"><p>Save</p></div>
              <div id="delete"><p onClick={handleDeleteNote}>Delete</p></div>
            </div>
          </div>
          <textarea id="body" placeholder="Your Note Here" />
        </div>
      </div>
    );
  }
