import { Link } from 'react-router-dom';
import { BrowserRouter, Routes, Route, useNavigate } from 'react-router-dom';
import { useState } from 'react';

function Sidebar({ notes, onAddNote, activeNote, setActiveNote, setIsEditing, isEditing }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [noteExists, setNoteExists] = useState(true);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    setNoteExists(true); // reset noteExists when search term changes
  };

  return (
    <div className="app-sidebar">
      <div className="app-sidebar-header">
        <h1>Notes</h1>
        <p onClick={onAddNote} className="plus-symb">
          &#43;
        </p>
      </div>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search notes"
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>
      <div className="app-sidebar-notes">
        {notes
          .filter((note) => {
            const includesSearchTerm = note.title.toLowerCase().includes(searchTerm.toLowerCase());
            if (!includesSearchTerm) {
              setNoteExists(false);
            }
            return includesSearchTerm;
          })
          .map((note, index) => (
            <Link to={`/notes/${index + 1}`} key={note.id}>
              <div
                className={`app-sidebar-note ${
                  note.id === activeNote && 'active'
                }`}
                onClick={() => {
                  setActiveNote(note.id);
                  setIsEditing(false);
                }}
              >
                <div className="sidebar-note-title">
                  <strong>{note.title}</strong>
                </div>
                <small className="note-meta">
                  {new Date(note.dateCreated).toLocaleDateString('en-US', {
                    month: 'long',
                    day: 'numeric',
                    year: 'numeric',
                    hour: 'numeric',
                    minute: 'numeric',
                    hour12: true,
                  })}
                </small>
                <p>
                  {note.body
                    ? note.body.length < 0
                      ? '...'
                      : `${note.body
                          .substr(0, 45)
                          .replace(/<\/?[^>]+(>|$)/g, '')}` + '...'
                    : '...'}
                </p>
              </div>
            </Link>
          ))}
        {!noteExists && <div>No notes found</div>}
      </div>
    </div>
  );
}

export default Sidebar;
