import React, { useState, useEffect } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

function Main({activeNote, onDeleteNote, onUpdateNote}) {
  const [cleanBody, setCleanBody] = useState(removeTags(activeNote.body));
  useEffect(() => {
    setCleanBody(removeTags(activeNote.body));
  }, [activeNote]);

  if (!activeNote) {
    return <div className="not-active-note"><small>No Note Selected</small></div>;
  }

  const onEdit = (key, value) => {
    onUpdateNote({
      ...activeNote,
      [key]: value,
      dateCreated: Date.now(),
    });
  };

  // Function to remove HTML tags from a string
  const removeTags = (str) => {
    if (str === null || str === '') return false;
    else str = str.toString();
    return str.replace(/<[^>]*>/g, '');
  }

  // State to store the cleaned body value


  // Update the cleaned body value whenever the activeNote changes

  return (
    <div className="app-main">
      <div className="app-main-note-edit">
        <div className="cont-text">
          <div className="left-side">
            <input
              type="text"
              id="title"
              value={activeNote.title}
              onChange={(e) => onEdit("title", e.target.value)}
              autoFocus
            />
            <div className="date-note">
              <input
                className="input-date"
                type="datetime-local"
                value={new Date(Date.now() - new Date().getTimezoneOffset() * 60000)
                  .toISOString()
                  .slice(0, -8)}
                onChange={(e) => onEdit("dateCreated", e.target.value)}
              />
            </div>
          </div>
          <div className="right-side">
            <div id="save">
              <p>Save</p>
            </div>
            <div id="delete">
              <p onClick={() => onDeleteNote(activeNote.id)}>Delete</p>
            </div>
          </div>
          <div className="quill">
          </div>
        </div>
        <div className="quill">
          <ReactQuill
            className="quill-editor"
            value={activeNote.body}
            onChange={(value) => {
              setCleanBody(removeTags(value));
              onEdit("body", value);
            }}
          />
        </div>
        <div className="body-preview" dangerouslySetInnerHTML={{__html: cleanBody}}></div>
      </div>
    </div>
  );
}

export default Main;
