import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { useEffect, useState } from "react";
function Main({ activeNote, onDeleteNote, onUpdateNote }) {
  const [isDirty, setIsDirty] = useState(false);

  if (!activeNote) {
    return <div className="not-active-note"><small>No Note Selected</small></div>;
  }

  const onEdit = (key, value) => {
    setTempNote({
      ...tempNote,
      [key]: value,
      dateCreated: Date.now(),
    });
  };

  const onSave = () => {
    onUpdateNote(tempNote);
    localStorage.setItem('notes', JSON.stringify([...JSON.parse(localStorage.getItem('notes')).filter(n => n.id !== tempNote.id), tempNote]));
  };

  return (
    <div className="app-main">
      <div className="app-main-note-edit">
        <div className="cont-text">
          <div className="left-side">
            <input
              type="text"
              id="title"
              value={activeNote.title}
              onChange={(e) => onEdit("title", e.target.value)}
              autoFocus
            />
            <div className="date-note">
              <input
                className="input-date"
                type="datetime-local"
                value={new Date(Date.now() - new Date().getTimezoneOffset() * 60000)
                  .toISOString()
                  .slice(0, -8)}
                onChange={(e) => onEdit("dateCreated", e.target.value)}
              />
            </div>
          </div>
          <div className="right-side">
            <div id="save" onClick={onSave}>
              <p>Save</p>
            </div>
            <div id="delete">
              <p onClick={() => onDeleteNote(activeNote.id)}>Delete</p>
            </div>
          </div>
          <div className="quill"></div>
        </div>
        <div className="quill">
          <ReactQuill
            className="quill-editor"
            value={activeNote.body}
            onChange={(value) => onEdit("body", value)}
          />
        </div>
      </div>
    </div>
  );
}


export default Main;
