function Main({ activeNote, onDeleteNote, onUpdateNote }) {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(activeNote.title); // add title state
  const quillRef = useRef(null);

  const toggleEditMode = () => {
    setIsEditing(!isEditing);
    if (isEditing) {
      saveNote();
    }
  };

  const saveNote = () => {
    const newContent = quillRef.current.getEditor().root.innerHTML;
    onUpdateNote({
      ...activeNote,
      title, // add title to the updated note object
      body: newContent,
      dateCreated: Date.now(),
    });
  };

  const saveTitle = () => {
    onUpdateNote({
      ...activeNote,
      title, // add title to the updated note object
      dateCreated: Date.now(),
    });
  };

  const onTitleChange = (e) => {
    setTitle(e.target.value); // update title state on input change
  };

  if (!activeNote) {
    return <div className="not-active-note"><small>No Note Selected</small></div>;
  }

  const onEdit = (key, value) => {
    onUpdateNote({
      ...activeNote,
      [key]: value,
      dateCreated: Date.now(),
    });
  };

  return (
    <div className="app-main">
      <div className="app-main-note-edit">
        <div className="cont-text">
          <div className="left-side">
            <input
              type="text"
              id="title"
              value={title}
              onChange={onTitleChange} // add onChange event to save title in state
              autoFocus
            />
            <div className="date-note">
              <input
                className="input-date"
                type="datetime-local"
                value={new Date(Date.now() - new Date().getTimezoneOffset() * 60000)
                  .toISOString()
                  .slice(0, -8)}
                onChange={(e) => onEdit("dateCreated", e.target.value)}
              />
            </div>
          </div>
          <div className="right-side">
            <div id="save">
              <p onClick={toggleEditMode}>{isEditing ? 'Save' : 'Edit'}</p>
            </div>
            <div id="delete">
              <p onClick={() => onDeleteNote(activeNote.id)}>Delete</p>
            </div>
          </div>
        </div>
        {isEditing ? (
          <ReactQuill
            className="quill-editor"
            placeholder='Enter Your Note Here'
            value={activeNote.body}
            // onChange={(value) => onEdit("body", value)}
            ref={quillRef}
          />
        ) : (
          <p className="quill-content">{activeNote.body && activeNote.body.replace(/<[^>]+>/g, '')}</p>
        )}
      </div>
    </div>
  );
}

export default Main;
