import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

function Layout({notes,activeNote, onDeleteNote, onUpdateNote, onAddNote,setActiveNote }) {
    const noNotesMessage = notes.length === 0 && <div id="no-note">No Notes Yet</div>;

    const onEdit = (key, value) => {
    onUpdateNote({
      ...activeNote,
      [key]: value,
      dateCreated: Date.now(),
    });
  };

    return(


    <div className="header">
          <div className="app-main">
        <div className="app-main-note-edit">
          <div className="cont-text">
            <div className="left-side">
              <input
                type="text"
                id="title"
                value={activeNote.title}
                onChange={(e) => onEdit("title", e.target.value)}
                autoFocus
              />
              <div className="date-note">
                <input
                  className="input-date"
                  type="datetime-local"
                  value={new Date(Date.now() - new Date().getTimezoneOffset() * 60000)
                    .toISOString()
                    .slice(0, -8)}
                  onChange={(e) => onEdit("dateCreated", e.target.value)}
                />
              </div>

            </div>
            <div className="right-side">
              <div id="save">
                <p>Save</p>
              </div>
              <div id="delete">
                <p onClick={() => onDeleteNote(activeNote.id)}>Delete</p>
              </div>
            </div>
            <div className="quill">

              </div>

          </div>
          <div className="quill">
          <ReactQuill
                className="quill-editor"
                value={activeNote.body}
                onChange={(value) => onEdit("body", value)}

              />
              </div>
        </div>


      </div>

                    <div className="app-sidebar">
                        <div className="app-sidebar-header">
                            <h1>Notes</h1>
                            <p onClick={onAddNote} className="plus-symb">
                                &#43;
                            </p>
                        </div>
                        <div className="app-sidebar-notes">
                            {noNotesMessage}
                            {notes.map((note) => (
                                <div
                                    className={`app-sidebar-note ${note.id === activeNote && "active"}`}
                                    onClick={() => setActiveNote(note.id)}
                                    key={note.id}
                                >
                                    <div className="sidebar-note-title">
                                        <strong>{note.title}</strong>
                                    </div>
                                    <small className="note-meta">
                                        {new Date(note.dateCreated).toLocaleDateString("en-US", {
                                            month: "long",
                                            day: "numeric",
                                            year: "numeric",
                                            hour: "numeric",
                                            minute: "numeric",
                                            hour12: true,
                                        })}
                                    </small>
                                    <p>{note.body && note.body.substr(0, 20) + "..."}</p>
                                </div>
                            ))}
                        </div>
                    </div>


                </div>













)



}

export default Layout;
