function Main({ activeNote, onDeleteNote, onUpdateNote }) {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState('');
  const quillRef = useRef(null);

  useEffect(() => {
    if (activeNote) {
      setTitle(activeNote.title);
    }
  }, [activeNote]);

  const handleSave = () => {
    saveNote();
    setIsEditing(false);
  };

  const saveNote = () => {
    const newContent = quillRef.current.getEditor().root.innerHTML;
    onUpdateNote({
      ...activeNote,
      body: newContent,
      dateCreated: Date.now(),
      title: title
    });
  };

  return (
    <div className="app-main">
      <div className="app-main-note-edit">
        <div className="cont-text">
          <div className="left-side">
            <input
              type="text"
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              autoFocus
            />
            <div className="date-note">
              <input
                className="input-date"
                type="datetime-local"
                defaultValue={new Date(Date.now() - new Date().getTimezoneOffset() * 60000)
                  .toISOString()
                  .slice(0, -8)}
              />
            </div>
          </div>
          <div className="right-side">
            <div id="save">
              {isEditing ? (
                <p onClick={handleSave}>Save</p>
              ) : (
                <p onClick={() => setIsEditing(true)}>Edit</p>
              )}
            </div>
            <div id="delete">
              <p onClick={() => onDeleteNote(activeNote.id)}>Delete</p>
            </div>
          </div>
        </div>
        {isEditing ? (
          <ReactQuill
            className="quill-editor"
            placeholder='Enter Your Note Here'
            value={activeNote.body}
            ref={quillRef}
          />
        ) : (
          <p className="quill-content">{activeNote.body && activeNote.body.replace(/<[^>]+>/g, '')}</p>
        )}
      </div>
    </div>
  );
}
