import {useEffect, useState} from "react";
import uuid from "react-uuid";
import Sidebar from "./Sidebar"
import Main from "./Main"


function App() {
  const [notes,setNotes]=useState(JSON.parse(localStorage.notes)|| []);
  const onAddNote=()=>{
    const remover = document.getElementById("no-note");
    remover.style.display = "none";
    const newNote = {
      id: uuid(),
      title:"Untitled",
      body:"",
      dateCreated: Date.now(),

    };
    setNotes([newNote,...notes]);
  };

  useEffect(()=>{
    localStorage.setItem("notes",JSON.stringify(notes));
  }, [notes]);


  const [activeNote,setActiveNote]=useState(false);

  // const [selectedNote,setSelectedNote]=useState(null);
  // const onNoteSelect = (note)=>{
  //   setSelectedNote(note);
  // };
  const onDeleteNote=(idToDelete) =>{
    const answer = window.confirm("Are you sure?");
    if (answer) {
      setNotes(notes.filter((note)=> note.id !== idToDelete));
    }


  };

  const getActiveNote = () => {
    return notes.find((note)=> note.id === activeNote);
  };

  const onUpdateNote=(updatedNote)=>{
    const arrayUpdated = notes.map((note)=>{
      if(note.id===activeNote){
        return updatedNote;
      }

      return note;
    });

    setNotes(arrayUpdated);
  };


  return ( <div className ="App">
    <div className="header"><div>
      <h1 className = "menu-symb">&#9776;</h1>
      </div><div className = "main-header">
         <h1>Lotion</h1><p><small>Like Notion, but worse</small></p>
         </div>
         </div>
         <hr>
         </hr>
  <div className="page">  

  <Sidebar notes={notes} 
    onAddNote={onAddNote} 
    activeNote={activeNote} 
    setActiveNote={setActiveNote} />
  <Main 
    activeNote={getActiveNote()}
    onDeleteNote={onDeleteNote}
    onUpdateNote={onUpdateNote}/> 
    </div>
    </div>);

}

export default App;
