import React, { useEffect,useState ,useRef} from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import {useNavigate} from 'react-router-dom';

function Main({ activeNote, onDeleteNote, onUpdateNote }) {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState('');

  const quillRef = useRef(null);

  useEffect(() => {
    if (activeNote) {
      setTitle(activeNote.title);
    }
  }, [activeNote]);

  const toggleEditMode = () => {
    setIsEditing(!isEditing);
    if (isEditing) {
      saveNote();

    }
  };
  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
    second: "numeric",
  };


  const formatDate = (when) => {
    const formatted = new Date(when).toLocaleString("en-US", options);
    if (formatted === "Invalid Date") {
      return "";
    }
    return formatted;
  };


  const saveNote = () => {
    const newContent = quillRef.current.getEditor().root.innerHTML;
    onUpdateNote({
      ...activeNote,
      body: newContent,
      dateCreated: Date.now(),
    });
  };

  const saveTitle = () => {
    onUpdateNote({
      ...activeNote,
      title,
      body: quillRef.current.getEditor().root.innerHTML,
      dateCreated: Date.now(),
    });
  };


  const onEdit = (key, value) => {
    onUpdateNote({
      ...activeNote,
      [key]: value,
      dateCreated: Date.now(),
    });
  };

  if (!activeNote) {
    return <div className="not-active-note"><small>No Note Selected</small></div>;
  }

  return (
    <div className="app-main">
      <div className="app-main-note-edit">
        <div className="cont-text">
          <div className="left-side">
            <input
              type="text"
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              onBlur={saveTitle}
              autoFocus
            />
            <div className="date-note">
              <input
                className="input-date"
                type="datetime-local"
                onChange = {formatDate()}
                defaultValue={new Date(Date.now() - new Date().getTimezoneOffset() * 60000)
                  .toISOString()
                  .slice(0, -8)}
                // onChange={(e) => onEdit("dateCreated", e.target.value)}
              />
            </div>
          </div>
          <div className="right-side">
            <div id="save">
              <p onClick={toggleEditMode}>{isEditing ? 'Save' : 'Edit'}</p>
            </div>
            <div id="delete">
              <p onClick={() => onDeleteNote(activeNote.id)}>Delete</p>
            </div>
          </div>
        </div>

        {isEditing ? (
          <ReactQuill
            className="quill-editor"
            placeholder='Enter Your Note Here'
            value={activeNote.body}
            // onChange={(value) => onEdit("body", value)}
            ref={quillRef}
          />
        ) : (
          <p className="quill-content">{activeNote.body && activeNote.body.replace(/<[^>]+>/g, '')}</p>
        )}
      </div>
    </div>
  );
}

export default Main;
