import React, { useEffect,useState ,useRef} from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import {useNavigate} from 'react-router-dom';

function Main({ activeNote, onDeleteNote, onUpdateNote }) {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState('');
  const [editorChanged, setEditorChanged] = useState(false); // new state variable

  const quillRef = useRef(null);

  useEffect(() => {
    if (activeNote) {
      setTitle(activeNote.title);
      setEditorChanged(false); // reset editorChanged state when a new note is selected
    }
  }, [activeNote]);

  const toggleEditMode = () => {
    setIsEditing(!isEditing);
    if (isEditing) {
      if (editorChanged) { // check if editor content has changed before saving the note
        saveNote();
      }
    }
  };

  const saveNote = () => {
    const newContent = quillRef.current.getEditor().root.innerHTML;
    const newDate = document.getElementById('date-input').value;
    const quill = quillRef.current.getEditor();
    const plainText = quill.getText();
    onUpdateNote({
      ...activeNote,
      title: title,
      formattedBody: newContent,
      body: plainText,
      dateCreated: newDate,
    });
    setEditorChanged(false); // reset editorChanged state after saving the note
  };

  const onEditorChange = (value, delta, source, editor) => {
    if (source === 'user') {
      setEditorChanged(true); // set editorChanged state to true when editor content is changed by the user
    }
  };


  // const saveTitle = () => {

  //   onUpdateNote({
  //     ...activeNote,
  //     title: title,
  //     dateCreated: Date.now(),
  //   });
  // };

  const onEdit = (key, value) => {
    onUpdateNote({
      ...activeNote,
      [key]: value,
      dateCreated: Date.now(),
    });
  };

  if (!activeNote) {
    return <div className="not-active-note"><small>No Note Selected</small></div>;
  }

  return (
    <div className="app-main">
      <div className="app-main-note-edit">
        <div className="cont-text">
        {isEditing ? (
  <div className="left-side">
    <input
      type="text"
      id="title"
      value={title}
      onChange={(e) => setTitle(e.target.value)}
      autoFocus
    />
    <div className="date-note">
      <input
        className="input-date"
        id="date-input"
        type="datetime-local"
        onChange={formatDate()}
        defaultValue={new Date(
          Date.now() - new Date().getTimezoneOffset() * 60000
        )
          .toISOString()
          .slice(0, -8)}
      />
    </div>
  </div>
) : (
  <div className="left-side">
    <h2>{title}</h2>
    <div className="date-note">
      <p>{formatDate(activeNote.dateCreated)}</p>
    </div>
  </div>
)}

          <div className="right-side">
            <div id="save">
              <p onClick={toggleEditMode}>{isEditing ? 'Save' : 'Edit'}</p>
            </div>
            <div id="delete">
              <p onClick={() => onDeleteNote(activeNote.id)}>Delete</p>
            </div>
          </div>
        </div>

        {isEditing ? (
          <ReactQuill
            className="quill-editor"
            placeholder='Enter Your Note Here'
            value={activeNote.formattedBody}
            // onChange={(value) => onEdit("body", value)}
            ref={quillRef}
          />
        ) : (
          <div className="quill-content" dangerouslySetInnerHTML={{__html: activeNote.formattedBody}}></div>
        )}
      </div>
    </div>
  );
}

export default Main;
