import React, { useState, useEffect } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import {useNavigate} from 'react-router-dom';

function Main({ activeNote, onDeleteNote, onUpdateNote }) {
  const [isEditing, setIsEditing] = useState(false);
  const [noteDraft, setNoteDraft] = useState(activeNote);

  useEffect(() => {
    setNoteDraft(activeNote);
  }, [activeNote]);

  if (!activeNote) {
    return <div className="not-active-note"><small>No Note Selected</small></div>;
  }

  const onEdit = (key, value) => {
    setNoteDraft({
      ...noteDraft,
      [key]: value,
      dateCreated: Date.now(),
    });
  };

  const onSave = () => {
    onUpdateNote(noteDraft);
    setIsEditing(false);
  };

  return (
    <div className="app-main">
      <div className="app-main-note-edit">
        <div className="cont-text">
          <div className="left-side">
            <input
              type="text"
              id="title"
              value={noteDraft.title}
              onChange={(e) => onEdit("title", e.target.value)}
              autoFocus
            />
            <div className="date-note">
              <input
                className="input-date"
                type="datetime-local"
                value={new Date(noteDraft.dateCreated).toISOString().slice(0, -8)}
                onChange={(e) => onEdit("dateCreated", e.target.value)}
              />
            </div>
          </div>
          <div className="right-side">
            {isEditing ? (
              <div id="save">
                <p onClick={onSave}>Save</p>
              </div>
            ) : (
              <div id="edit">
                <p onClick={() => setIsEditing(true)}>Edit</p>
              </div>
            )}
            <div id="delete">
              <p onClick={() => onDeleteNote(activeNote.id)}>Delete</p>
            </div>
          </div>
        </div>
        {isEditing ? (
          <ReactQuill
            className="quill-editor"
            placeholder='Enter Your Note Here'
            value={noteDraft.body}
            onChange={(value) => onEdit("body", value)}
          />
        ) : (
          <p className="quill-content">{noteDraft.body && noteDraft.body.replace(/<[^>]+>/g, '')}</p>
        )}
      </div>
    </div>
  );
}

export default Main;
