function Main({activeNote,onDeleteNote,onUpdateNote}){

    if(!activeNote)return <div className="not-active-note">No Note Selected</div>





    const onEdit = (key,value)=> {
        onUpdateNote({
            id:activeNote.id,
            [key]:value,
            dateCreated:Date.now(),
        })
    }
return (

<div className="app-main">
    <div className="app-main-note-edit">
        <div className="cont-text">
        <div className="left-side">
        <input type ="text" id ="title" value = {title} onChange = {(e)=> onEdit("title",e.target.value)}autoFocus/>
        <div className="date-note">
  <small>
    {new Date(date).toLocaleString("en-US", {
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit"
    }).replace(/\//g, "-")}
  </small>
</div>



        </div>
        <div className="right-side">
        <div id="save"><p>Save</p></div>
        <div id="delete"><p onClick={()=>
            onDeleteNote(activeNote.id)}>Delete</p></div>
        </div>
        </div>

        <textarea id="body" placeholder="Your Note Here" onChange = {(e)=> onEdit("body",e.target.value)}/>
    </div>
</div>);
}

export default Main;
