function Main({activeNote,onDeleteNote,onUpdateNote}){

    if(!activeNote)return <div className="not-active-note"><small>No Note Selected</small></div>


    const onEdit = (key,value)=> {
        onUpdateNote({
            ...activeNote,
            [key]:value,
            dateCreated:Date.now(),
        })
    }

      	var now = new Date();
	var utcString = now.toISOString().substring(0,19);
	var year = now.getFullYear();
	var month = now.getMonth() + 1;
	var day = now.getDate();
	var hour = now.getHours();
	var minute = now.getMinutes();
	var second = now.getSeconds();
	var localDatetime = year + "-" +
    (month < 10 ? "0" + month.toString() : month) + "-" +
    (day < 10 ? "0" + day.toString() : day) + "T" +
    (hour < 10 ? "0" + hour.toString() : hour) + ":" +
    (minute < 10 ? "0" + minute.toString() : minute) +
    utcString.substring(16,19);
	var datetimeField = document.getElementById("myDatetimeField");
	datetimeField.value = localDatetime;
return (

<div className="app-main">
    <div className="app-main-note-edit">
        <div className="cont-text">
        <div className="left-side">
        <input type ="text" id ="title" value = {activeNote.title} onChange = {(e)=> onEdit("title",e.target.value)}autoFocus/>
        <div className="date-note">

        {/* <input className="input-date" type="datetime-local"/> */}
        <input type="datetime-local" id="myDatetimeField"/>

  {/* <small>
    {new Date(activeNote.dateCreated).toLocaleDateString("en-US", {
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit"
    }).replace(/\//g, "-")}
  </small> */}
</div>




        </div>
        <div className="right-side">
        <div id="save"><p>Save</p></div>
        <div id="delete"><p onClick={()=>
            onDeleteNote(activeNote.id)}>Delete</p></div>
        </div>
        </div>

        <textarea id="body" value={activeNote.body} placeholder="Your Note Here" onChange = {(e)=> onEdit("body",e.target.value)}/>
    </div>
</div>);
}

export default Main;
