import {Link} from 'react-router-dom'
import {BrowserRouter,Routes,Route, useNavigate} from 'react-router-dom';






function Sidebar({ notes, onAddNote, activeNote, setActiveNote,setIsEditing,isEditing}) {
  const noNotesMessage = notes.length === 0 && <div id="no-note">No Notes Yet</div>;
  const navigate = useNavigate();


  return (
    <div className="app-sidebar">
      <div className="app-sidebar-header">
        <h1>Notes</h1>
        <p onClick={onAddNote} className="plus-symb">
          &#43;
        </p>

      </div>
      <div className="app-sidebar-notes">
        {noNotesMessage}
        {notes.map((note, index) => (

          <Link to = {`/notes/${index +1}` } >
          <div
            className={`app-sidebar-note ${note.id === activeNote && "active"}`}
            onClick={() => {
              setActiveNote(note.id);
              setActiveNote(false);
            }}

            key={note.id}
          >
            <div className="sidebar-note-title">
              <strong>{note.title}</strong>
            </div>
            <small className="note-meta">
              {new Date(note.dateCreated).toLocaleDateString("en-US", {
                month: "long",
                day: "numeric",
                year: "numeric",
                hour: "numeric",
                minute: "numeric",
                hour12: true,
              })}
            </small>
            <p>{note.body ? (note.body.length < 0 ? '...' : `${note.body.substr(0, 45).replace(/<\/?[^>]+(>|$)/g, "")}` + '...') : '...'}</p>

          </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
export default Sidebar;
