import {Link} from 'react-router-dom'






function Sidebar({ notes, onAddNote, activeNote, setActiveNote }) {
  const noNotesMessage = notes.length === 0 && <div id="no-note">No Notes Yet</div>;
   return (
    <div className="app-sidebar">
      <div className="app-sidebar-header">
        <h1>Notes</h1>
        <Link to="" onClick={onAddNote} className="plus-symb">
          &#43;
        </Link>

      </div>
      <div className="app-sidebar-notes">
        {noNotesMessage}
        {notes.map((note) => (

          <Link to = {`/notes/${notes.indexOf(note)+1}`}>
          <div
            className={`app-sidebar-note ${note.id === activeNote && "active"}`}
            onClick={() => setActiveNote(note.id)}
            key={note.id}
          >
            <div className="sidebar-note-title">
              <strong>{note.title}</strong>
            </div>
            <small className="note-meta">
              {new Date(note.dateCreated).toLocaleDateString("en-US", {
                month: "long",
                day: "numeric",
                year: "numeric",
                hour: "numeric",
                minute: "numeric",
                hour12: true,
              })}
            </small>
            <p>{note.body && note.body.replace(/<[^>]+>/g, '').substr(0, 20) + "..."}</p>

          </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
export default Sidebar;
