function Main({activeNote, onDeleteNote, onUpdateNote}) {
    if (!activeNote) {
      return <div className="not-active-note"><small>No Note Selected</small></div>;
    }

    const [dateCreated, setDateCreated] = useState(new Date().toLocaleString('en-US', {timeZone: 'America/Edmonton'}));

    const onEdit = (key, value) => {
      onUpdateNote({
        ...activeNote,
        [key]: value,
        dateCreated: Date.now(),
      });
    };

    return (
      <div className="app-main">
        <div className="app-main-note-edit">
          <div className="cont-text">
            <div className="left-side">
              <input type="text" id="title" value={activeNote.title} onChange={(e) => onEdit('title', e.target.value)} autoFocus />
              <div className="date-note">
                <input className="input-date" type="datetime-local" value={new Date(dateCreated).toISOString().slice(0, 16)} onChange={(e) => {
                  const date = new Date(e.target.value + ':00Z').toLocaleString('en-US', {timeZone: 'America/Edmonton'});
                  setDateCreated(date);
                  onEdit('dateCreated', new Date(date).getTime());
                }} />
                <small>{new Date(dateCreated).toLocaleString()}</small>
              </div>
            </div>
            <div className="right-side">
              <div id="save"><p>Save</p></div>
              <div id="delete"><p onClick={() => onDeleteNote(activeNote.id)}>Delete</p></div>
            </div>
          </div>
          <textarea id="body" value={activeNote.body} placeholder="Your Note Here" onChange={(e) => onEdit('body', e.target.value)} />
        </div>
      </div>
    );
  }

  export default Main;
